import React, {useState} from "react";

const AddItem = (props) => {
  return(
    <div>
      <span>{props.name}</span>
      <span>{props.age}</span>
      <button onClick={()=> console.log(props.id)}>remove</button>
    </div>
  )
}

const App = () => {

  const [item, setItem] = useState({
    id: Math.random(),
    userName: "",
    age: ""
  })

  const [itemList, setItemList] = useState([])

  function handleChange(e) {
    setItem(prev => {
      return({
        ...prev,
        [e.target.name]: e.target.value
      })
    })
  }

  function handleSubmit(e) {
    e.preventDefault()
    setItem(prev => ({...prev, id:Math.random()}))
    setItemList(prev => [...prev, item])
  }

  function removeItem(id){
    for(let i=0; i<itemList.length; i++){
      if(id==itemList[i].id){
        setItemList(itemList.splice(1,i))
      }
    }
  }


  console.log(item);
  console.log(itemList);

  const renderUser = itemList.map(x => {
    return(
      <AddItem 
      key={x.id}
      id={x.id}
      name={x.userName}
      age={x.age}
      func={removeItem}
      />
    )
  })

  return (
    <div >
      <form onSubmit={handleSubmit}>
        <label htmlFor="user">Username</label>
        <input 
        id="user"
        type="text" 
        placeholder="item"
        name="userName"
        value={item.userName}
        onChange={handleChange}
        />
        <label htmlFor="age">Age</label>
        <input 
        id="age"
        type="number" 
        placeholder="age"
        name="age"
        value={item.age}
        onChange={handleChange}
        />
        <button>add user</button>
      </form>
      {renderUser}
    </div>
  );
}

export default App;
