import React, {useState, useEffect} from "react";


const AddItem = (props) => {
  return(
    <div>
      <span>{props.name}</span>
      <span>{props.age}</span>
      <button onClick={(e)=> {e.preventDefault(); props.func(props.id)}}>remove</button>
    </div>
  )
}

const Finished = (props) => {
  return(
    <div>
      <span>{props.name}</span>
      <span>{props.age}</span>
    </div>
  )
}

const ErrorModal = (props) => {
  return (
    <div>
      <h1>{props.title}</h1>
      <p>{props.message}</p>
    </div>
  )
}

const App = () => {

  const [item, setItem] = useState({
    id: 0,
    userName: "",
    age: ""
  })

  const [darkTheme, setDarkTheme] = useState(false)
  const [itemList, setItemList] = useState([])
  const [finishedItems, setFinishedItems] = useState([])
  const [showFinished, setShowFinished] = useState(false)
  const [err, setErr] = useState(false)

  function handleChange(e) {
    setItem(prev => {
      return({
        ...prev,
        [e.target.name]: e.target.value
      })
    })
  }

  function handleSubmit(e) {
    e.preventDefault()
    if(item.userName=="" || item.age==""){
      setErr(true)
    }
    console.log(err);
    setItem(prev => ({id:Math.random(), userName:"", age:""}))
    setItemList(prev => [...prev, item])
  }

  function removeItem2(id) {
    let ls=[]
    let x;
    for(let i=0; i<itemList.length; i++){
      if(id===itemList[i].id){
        ls=itemList.filter(value => value!==itemList[i])
        x=itemList[i]
      }
    }
    setItemList(ls)
    setFinishedItems(prev => [...prev, x])
  }



  const renderUser = itemList.map(x => {
    return(
      <AddItem 
      key={x.id}
      id={x.id}
      name={x.userName}
      age={x.age}
      func={removeItem2}
      />
    )
  })

  const renderFinishedItems = finishedItems.map(x => {
    return(
      <Finished 
      key={x.id}
      name={x.userName}
      age={x.age}
      />
    )
  }) 

  const dark = darkTheme ? "blue" : "white"

  useEffect(() => {
      document.body.style.background = dark
    },[color]
  )




  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label htmlFor="user">Username</label>
        <input 
        id="user"
        type="text" 
        placeholder="item"
        name="userName"
        value={item.userName}
        onChange={handleChange}
        />
        <label htmlFor="age">Age</label>
        <input 
        id="age"
        type="number" 
        placeholder="age"
        name="age"
        value={item.age}
        onChange={handleChange}
        />
        <button>add user</button>
      </form>
      {renderUser}
      <button onClick={()=> setShowFinished(prev=>!prev)}>Show finished items</button>
      {showFinished && <div>{renderFinishedItems}</div>}
      {err && <ErrorModal title="error" message="error"/>}
      <button onClick={()=>setDarkTheme(prev=>!prev)}>toggle</button>
    </div>
  );
}

export default App;
